App.views.Catalog=Ext.extend(Ext.Panel,{layout:"fit",scroll:true,initComponent:function(){this.store=App.stores.Specimen;this.list=new Ext.List({fullscreen:true,defaults:{scope:this},scroll:"vertical",title:this.title,store:this.store,emptyText:this.emptyText,itemTpl:Ext.XTemplate.from("specimen-list")});this.items=[this.list];App.views.Catalog.superclass.initComponent.call(this);this.list.on("itemtap",this.onItemTap,this)},doShow:function(){},onItemTap:function(d,c,f,g){var b=d.getStore();var a=b.getAt(c).data;App.viewport.navTo("specimen",a,"catalog")}});